package com.bayamp.trainning.java.arrays;

public class SimpleArrayProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        String str = "The grass is green on other side";
        System.out.println("The full string -> "+str);
        System.out.println("The string length -> "+str.length());
        System.out.println("The first five chars -> "+str.substring(0, 5));
        System.out.println("The last five chars -> "+ str.substring(5, 30 ));
        System.out.println("Captilization -> "+ str.toUpperCase());
        

	}

}
